    if (arr.length == 0) {
        return -1;
    } else {
        int low = 0;
        int high = arr.length;
        int mid = low + (high - low) / 2;
        //@ maintaining 0 <= low && low <= high  && high <= arr.length && mid == low + (high - low) / 2;
