        //@ maintaining (\forall int i; high <= i && i < arr.length ==> key < arr[i]);
        //@ decreases high - low;
        while (low < high && arr[mid] != key) {
            if (arr[mid] < key) {
                low = mid + 1;
            } else {
                high = mid;
            }
            mid = low + (high - low) / 2;
        }
        if (low >= high) {
            return -1;
        }
        return mid;
    }


}

