                    && cases[i][1] == cases[i][2])
                    || (cases[0][j] == cases[1][j]    // colonne pleine
                    && cases[1][j] == cases[2][j])
                    || (i == j    // première diagonale pleine
                    && cases[0][0] == cases[1][1]
                    && cases[1][1] == cases[2][2])
                    || (i + j == 2    // deuxième diagonale pleine
                    && cases[0][2] == cases[1][1]
                    && cases[1][1] == cases[2][0]));
}

