private void _set(int q, int a) {
	// Initialiser l'année
	this.annee = a;

	// Initialiser le mois
	this.mois = 1;
	while (q > nbJoursDansMois(this.mois, a)) { //  le mois est terminé
		q = q - nbJoursDansMois(this.mois, a);
		this.mois++;
	}

	// Initialiser les jours
	this.jour = q;
}

