  }

  /**
   * Returns a short string representation of the attribute type.
   * 
   * @param type	the type of the attribute
   * @return		the string representation of the attribute type
   */
  public static String typeToStringShort(int type) {
    String	result;

    switch(type) {
      case NUMERIC:
	result = "Num";
	break;

      case NOMINAL:
	result = "Nom";
	break;

      case STRING:
	result = "Str";
	break;

      case DATE:
	result = "Dat";
	break;

      case RELATIONAL:
